changeSet.createProcedure:
create function ${database.defaultSchemaName}.update_records_set_has_marc_record() returns trigger
    language plpgsql
as
$$
BEGIN
    IF (TG_OP = 'DELETE') THEN
      UPDATE ${database.defaultSchemaName}.records_lb SET has_parsed_record = false WHERE id = OLD.id;
      RETURN OLD;
    ELSE
      UPDATE ${database.defaultSchemaName}.records_lb SET has_parsed_record = true WHERE id = NEW.id;
      RETURN NEW;
    END IF;
END;
$$;
