changeSet.sql:
create or replace function ${database.defaultSchemaName}.insert_marc_indexers()
    returns trigger as $insert_marc_indexers$
begin
    if (TG_OP = 'UPDATE') then
        delete from ${database.defaultSchemaName}.marc_indexers where marc_id = NEW.id;
        delete from ${database.defaultSchemaName}.marc_indexers_leader where marc_id = NEW.id;
    end if;
    perform ${database.defaultSchemaName}.fill_in_marc_indexers(NEW.id, NEW.content);
    return NEW;
end;
$insert_marc_indexers$ language plpgsql;

create or replace function ${database.defaultSchemaName}.delete_marc_indexers()
    returns trigger as $delete_marc_indexers$
begin
    if (TG_OP = 'DELETE') then
        delete from ${database.defaultSchemaName}.marc_indexers where marc_id = OLD.id;
        delete from ${database.defaultSchemaName}.marc_indexers_leader where marc_id = OLD.id;
    end if;
    return OLD;
end;
$delete_marc_indexers$ language plpgsql;

DROP TRIGGER IF EXISTS ${database.defaultSchemaName}.process_marc_records_lb_delete_trigger ON ${database.defaultSchemaName}.marc_records_lb CASCADE;
DROP TRIGGER IF EXISTS ${database.defaultSchemaName}.process_marc_records_lb_insert_update_trigger ON ${database.defaultSchemaName}.marc_records_lb CASCADE;
create trigger ${database.defaultSchemaName}.process_marc_records_lb_delete_trigger before delete on ${database.defaultSchemaName}.marc_records_lb for each row execute function ${database.defaultSchemaName}.delete_marc_indexers();
create trigger ${database.defaultSchemaName}.process_marc_records_lb_insert_update_trigger after insert or update on ${database.defaultSchemaName}.marc_records_lb for each row execute function ${database.defaultSchemaName}.insert_marc_indexers();
