changeSet.sql:
do $$ declare
  ind integer;
  suffix text;
begin
  for ind in 0 .. 999
    loop
      suffix = lpad(ind::text, 3, '0');
      execute 'create table ${database.defaultSchemaName}.marc_indexers_' || suffix || ' partition of marc_indexers for values in ('''|| suffix ||''');';
    end loop;
end; $$;
