changeSet.sql:
do $$ declare
  index integer;
  suffix text;
begin
  for index in 0 .. 999
    loop
      suffix = lpad(index::text, 3, '0');
      execute 'create index idx_marc_indexers_marc_id_' || suffix || ' on ${database.defaultSchemaName}.marc_indexers_' || suffix || '(marc_id);';
    end loop;
end;
$$;
